    p = 2
    while (p * p <= limit):
        if (is_prime[p] == True):
            for i in range(p * p, limit + 1, p):
                is_prime[i] = False
        p += 1
    return [p for p in range(2, limit + 1) if is_prime[p]]

def prime_factors(n):
    """Returns a list of prime factors of the given number."""
    factors = []
    for p in sieve_of_eratosthenes(int(n**0.5) + 1):
        while n % p == 0:
            factors.append(p)
            n //= p
    if n > 1:
        factors.append(n)
    return factors

def euler_totient(n):
    """Returns Euler's totient function value for the given number."""
    return len([i for i in range(1, n + 1) if gcd(i, n) == 1])

def count_connected_graphs(N, P):
    """Returns the number of connected graphs with N vertices and M edges modulo P."""
    if N % 2 != 0:
        raise ValueError("N must be even")
    if not is_prime(P):
        raise ValueError("P must be a prime number")

    def is_connected_graph(n, m):
        """Returns True if the graph is connected, False otherwise."""
        if m < n - 1:
            return False
        if m == n - 1:
            return True
        if m == n:
            return False

        # Generate all possible edges
        edges = set(combinations(range(1, n + 1), 2))
        # Generate all possible subsets of edges
        for subset in range(1, 2 ** (m - n + 1)):
            # Convert subset to set of edges
            subset_edges = set()
            for i in range(m - n + 1):
                if (subset >> i) & 1:
                    subset_edges.add(edges[i])
            # Check if the graph is connected
            if len(subset_edges) == n - 1:
                return True
        return False

    # Generate all possible M values
    M_values = [N - 1] + list(range(N * (N - 1) // 2))
    # Count the number of connected graphs for each M value
    counts = [0] * len(M_values)
    for i, M in enumerate(M_values):
        for j in range(1, M + 1):
            if is_connected_graph(N, j):
                counts[i] += 1
    # Return the counts modulo P
    return tuple(counts[i] % P for i in range(len(counts)))
