        while n % p == 0:
            factors.append(p)
            n //= p
    if n > 1:
        factors.append(n)
    return factors

def euler_totient(n, primes):
    """
    Calculate Euler's Totient function for a given number n using the prime factorization.
    """
    result = n
    for p in primes:
        if n % p == 0:
            result -= result // p
            while n % p == 0:
                n //= p
    if n > 1:
        result -= result // n
    return result

