def majority_element(arr):
    """
    Returns the majority element in the array.
    If there is no majority element, returns None.
    """
    count = {}
    for num in arr:
        if num in count:
            count[num] += 1
        else:
            count[num] = 1
    for num, freq in count.items():
        if freq > len(arr) // 2:
            return num
    return None

def majority_element_group(arr):
    """
    Returns the majority element in each group of 3 elements.
    """
    return [majority_element(arr[i:i+3]) for i in range(0, len(arr), 3)]

def majority_element_string(s):
    """
    Returns the majority element in the binary string.
    """
    return majority_element_group([int(c) for c in s])

def min_changes_to_majority(A):
    """
    Returns the minimum number of changes required to make the majority element of A' equal to 1.
    """
    A_prime = ''.join(str(majority_element_string(A)[i]) for i in range(len(A) // 3))
    return sum(1 for i in range(len(A)) if A[i] != A_prime[i])

