from collections import defaultdict

def is_alkane(graph):
    """
    Determines if a given graph is an alkane.

    :param graph: A dictionary representing the graph where keys are vertices and values are lists of adjacent vertices.
    :return: True if the graph is an alkane, False otherwise.
    """
    degree = defaultdict(int)
    for u, neighbors in graph.items():
        for v in neighbors:
            degree[u] += 1
            degree[v] += 1

    for vertex, deg in degree.items():
        if deg not in [1, 4]:
            return False

    return True

def find_alkane_subgraph(graph):
    """
    Finds the maximum alkane subgraph in the given graph.

    :param graph: A dictionary representing the graph where keys are vertices and values are lists of adjacent vertices.
    :return: A tuple containing the maximum alkane subgraph and its size, or (-1, -1) if no alkane subgraph exists.
    """
    def dfs(vertex, parent):
        visited[vertex] = True
        subgraph[vertex] = [vertex]
        for neighbor in graph[vertex]:
            if not visited[neighbor]:
                if neighbor == parent:
                    continue
                if not dfs(neighbor, vertex):
                    return False
                subgraph[vertex].extend(subgraph[neighbor])
