def print_pattern(N):
    """
    Prints the N x N pattern as specified in the problem description.

    Parameters:
    N (int): The size of the grid.
    """
    pattern = [['.' for _ in range(N)] for _ in range(N)]

    for i in range(1, N + 1):
        j = N + 1 - i
        if i <= j:
            for x in range(i, j + 1):
                for y in range(i, j + 1):
                    pattern[x - 1][y - 1] = '#' if i % 2 == 1 else '.'

    for row in pattern:
        print(''.join(row))
