from collections import defaultdict

def min_cost_to_reach_vertex(N, M, X, edges):
    """
    Computes the minimum cost to reach vertex N from vertex 1 by performing the specified operations.

    :param N: Number of vertices in the graph.
    :param M: Number of edges in the graph.
    :param X: Cost to reverse all edges.
    :param edges: List of tuples representing directed edges (u, v).
    :return: Minimum cost to reach vertex N.
    """
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)

    visited = [False] * (N + 1)
    cost = [float('inf')] * (N + 1)
    cost[1] = 0

    while not visited[N]:
        min_cost = float('inf')
        next_vertex = -1

        for vertex in range(1, N + 1):
            if not visited[vertex] and cost[vertex] < min_cost:
                min_cost = cost[vertex]
                next_vertex = vertex

        if next_vertex == -1:
            return float('inf')

        visited[next_vertex] = True

        for neighbor in graph[next_vertex]:
            if not visited[neighbor]:
                new_cost = cost[next_vertex] + 1
                if new_cost < cost[neighbor]:
                    cost[neighbor] = new_cost

        if next_vertex in graph:
            for neighbor in graph[next_vertex]:
                if not visited[neighbor]:
                    new_cost = cost[next_vertex] + X
                    if new_cost < cost[neighbor]:
                        cost[neighbor] = new_cost

    return cost[N]
