from collections import defaultdict
from typing import List

def solve(N: int, M: int, edges: List[List[int]]) -> str:
    """
    Determines the winner of the game when both players play optimally.

    :param N: Number of vertices in the graph.
    :param M: Number of edges in the graph.
    :param edges: List of edges, each edge is a tuple (U, V) where U and V are vertices.
    :return: "Aoki" if Aoki wins, "Takahashi" if Takahashi wins.
    """
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def has_odd_cycle():
        """
        Checks if the graph contains an odd cycle.

        :return: True if the graph contains an odd cycle, False otherwise.
        """
        visited = [False] * (N + 1)
        parent = [-1] * (N + 1)
        def dfs(node, prev):
            visited[node] = True
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    if dfs(neighbor, node):
                        return True
                elif neighbor != prev:
                    return True
            return False
        for i in range(1, N + 1):
            if not visited[i]:
                if dfs(i, -1):
                    return True
        return False

    if has_odd_cycle():
        return "Takahashi"
    else:
        return "Aoki"
