from collections import defaultdict

def is_odd_cycle(graph):
    """
    Check if the graph contains an odd cycle.

    :param graph: A dictionary representing the graph where keys are vertices and values are lists of adjacent vertices.
    :return: True if the graph contains an odd cycle, False otherwise.
    """
    def dfs(node, parent):
        visited[node] = True
        for neighbor in graph[node]:
            if not visited[neighbor]:
                if dfs(neighbor, node):
                    return True
            elif neighbor != parent:
                return True
        return False

    visited = [False] * (len(graph) + 1)
    for node in range(1, len(graph) + 1):
        if not visited[node]:
            if dfs(node, -1):
                return True
    return False

def optimal_game_winner(N, M, edges):
    """
    Determine the winner of the game when both players play optimally.

