def count_good_integers(N):
    """
    Counts the number of good integers between 1 and N, inclusive.

    :param N: An integer representing the upper limit.
    :return: An integer representing the count of good integers.
    """
    count = 0
    for i in range(1, N + 1):
        for j in range(1, int(i**0.5) + 1):
            if i % (2 ** j) == 0:
                b = i // (2 ** j)
                if b % 2 == 0:
                    count += 1
                    break
    return count

