def is_400_number(n):
    """
    Check if a number is a 400 number.

    A 400 number has exactly 2 distinct prime factors and each prime factor divides n an even number of times.

    :param n: Integer to check
    :return: True if n is a 400 number, False otherwise
    """
    if n < 2:
        return False

    factors = {}
    for i in range(2, int(n**0.5) + 1):
        while n % i == 0:
            if i in factors:
                factors[i] += 1
            else:
                factors[i] = 1
            n //= i

    if n > 1:
        if n in factors:
            factors[n] += 1
        else:
            factors[n] = 1

    return len(factors) == 2 and all(factors[p] % 2 == 0 for p in factors)

