def solve_problem(N, M, operations):
    """
    Solves the problem of minimizing the cost to make x_1 = x_2 = ... = x_N = 1.

    Parameters:
    N (int): The length of the sequence.
    M (int): The number of operations.
    operations (list of tuples): Each tuple (L_i, R_i) represents an operation.

    Returns:
    str: A string representing the operations to achieve the goal with the minimum cost.
    """
    x = [0] * N
    cost = 0

    for L, R in operations:
        if L == R:
            if x[L - 1] == 0:
                cost += 1
                x[L - 1] = 1
        else:
            if x[L - 1] == 0:
                cost += 1
                x[L - 1] = 1
            if x[R - 1] == 0:
                cost += 1
                x[R - 1] = 1

    if x == [1] * N:
        return f"{cost}\n{operations}"
    else:
        return "-1"
