from collections import defaultdict, deque

def can_reach(T, S, graph):
    """
    Determines if it is possible to move piece A to vertex T and piece B to vertex S
    by moving either piece A or piece B to an adjacent vertex via an edge.

    :param T: Target vertex for piece A.
    :param S: Source vertex for piece B.
    :param graph: Adjacency list representation of the graph.
    :return: True if it is possible to reach the goal, False otherwise.
    """
    visited = set()
    queue = deque([(S, 'B')])

    while queue:
        vertex, piece = queue.popleft()
        if vertex == T and piece == 'A':
            return True
        if vertex in visited:
            continue
        visited.add(vertex)
        for neighbor in graph[vertex]:
            if piece == 'A' and neighbor == T:
                return True
            elif piece == 'B' and neighbor == S:
                return True
            if piece == 'A' and neighbor != T:
                queue.append((neighbor, 'B'))
            elif piece == 'B' and neighbor != S:
                queue.append((neighbor, 'A'))
    return False

def min_moves_to_reach(T, S, graph):
    """
    Computes the minimum number of moves required to move piece A to vertex T and piece B to vertex S.

    :param T: Target vertex for piece A.
    :param S: Source vertex for piece B.
    :param graph: Adjacency list representation of the graph.
    :return: Minimum number of moves required, or -1 if it is impossible.
    """
    if not can_reach(T, S, graph):
        return -1

    visited = set()
    queue = deque([(S, 'B')])
    moves = 0

    while queue:
        for _ in range(len(queue)):
            vertex, piece = queue.popleft()
            if vertex == T and piece == 'A':
                return moves
            if vertex in visited:
                continue
            visited.add(vertex)
            for neighbor in graph[vertex]:
                if piece == 'A' and neighbor == T:
                    return moves + 1
                elif piece == 'B' and neighbor == S:
                    return moves + 1
                if piece == 'A' and neighbor != T:
                    queue.append((neighbor, 'B'))
                elif piece == 'B' and neighbor != S:
                    queue.append((neighbor, 'A'))
        moves += 1
