    edges = []
    for i in range(N):
        L, R = map(int, data[N + 1 + i * 2:N + 2 + i * 2 + 2])
        edges.append((L, R))

    Q = int(data[N * 2 + 2 * N])
    queries = []
    for i in range(Q):
        s, t = map(int, data[N * 2 + 2 * N + 1 + i * 2:N * 2 + 2 * N + 1 + i * 2 + 3])
        queries.append((s, t))

    G = defaultdict(dict)
    for L, R in edges:
        for i in range(L, R + 1):
            for j in range(L, R + 1):
                if i != j:
                    G[i][j] = 0
                    G[j][i] = 0

    for L, R in edges:
        for i in range(L, R + 1):
            for j in range(L, R + 1):
                if i != j:
                    G[i][j] = weights[i - 1]
                    G[j][i] = weights[i - 1]

