def count_distinct_strings(N, S):
    """
    Counts the number of distinct strings S that can be obtained by performing the operation any number of times.
    """
    MOD = 998244353
    dp = [0] * (1 << N)
    dp[0] = 1

    for mask in range(1 << N):
        if is_valid_parenthesis(S[mask]):
            dp[mask] = (dp[mask] + 1) % MOD
        else:
            for i in range(N):
                if mask & (1 << i):
                    for j in range(i + 1, N):
                        if mask & (1 << j):
                            new_mask = mask ^ (1 << i) ^ (1 << j)
                            if is_valid_parenthesis(S[new_mask]):
                                dp[new_mask] = (dp[new_mask] + dp[mask]) % MOD
    return dp[(1 << N) - 1]

