        Generates a random board with the specified number of red and blue pieces.
        """
        board = [['.' for _ in range(10**9 + 1)] for _ in range(10**9 + 1)]
        red_positions = []
        blue_positions = []
        for _ in range(red_count):
            r = random.randint(1, 10**9)
            c = random.randint(1, 10**9)
            while board[r][c] != '.':
                r = random.randint(1, 10**9)
                c = random.randint(1, 10**9)
            place_piece(board, red_positions, blue_positions, r, c, 'R')
        for _ in range(blue_count):
            r = random.randint(1, 10**9)
            c = random.randint(1, 10**9)
            while board[r][c] != '.':
                r = random.randint(1, 10**9)
                c = random.randint(1, 10**9)
            place_piece(board, red_positions, blue_positions, r, c, 'B')
