def solve(N, S):
    """
    Solves the problem of counting the number of ways to partition the vertices of a directed graph into N pairs
    such that the final graph is strongly connected, modulo 998244353.

    :param N: Number of vertices in the graph.
    :param S: A string of length 2N consisting of N 'W's and N 'B's.
    :return: The number of ways to partition the vertices into N pairs such that the graph is strongly connected.
    """
    from collections import defaultdict
    from itertools import combinations

    # Create a graph with vertices 1 to 2N and edges (i, i+1)
    graph = defaultdict(list)
    for i in range(2 * N):
        graph[i].append(i + 1)

    # Create a list of vertices with 'W' and 'B'
    vertices = [i for i in range(1, 2 * N + 1) if S[i - 1] == 'W']

    # Generate all possible ways to pair up the vertices
    def generate_pairs(vertices):
        if not vertices:
            yield []
        for i in range(1, len(vertices) + 1):
            for pair in generate_pairs(vertices[i:]):
                yield [vertices[:i]] + [pair]

    # Count the number of ways to partition the vertices into N pairs such that the graph is strongly connected
    def count_ways(vertices, pairs):
        if not vertices:
            return 1
        if len(vertices) == 1:
            return 1 if len(pairs) == 1 and pairs[0] == [] else 0
        count = 0
        for pair in pairs:
            if len(pair) == 0:
                count += count_ways(vertices[1:], [vertices[:1]] + [pair])
            else:
                count += count_ways(vertices[1:], [vertices[:1]] + [pair + [vertices[0]]])
        return count

    # Count the number of ways to partition the vertices into N pairs such that the graph is strongly connected
    count = 0
    for pairs in generate_pairs(vertices):
        if len(pairs) == N:
            count += count_ways(vertices, pairs)

    return count % 998244353
