    def countNonDecreasingSubarrays(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)
        dp = [0] * (n + 1)
        dp[0] = 1
        for i in range(1, n + 1):
            dp[i] = dp[i - 1] + (i - 1) * (i - 2) // 2
            if i > 1 and nums[i - 1] >= nums[i - 2]:
                dp[i] += dp[i - 2]
            else:
                dp[i] += dp[i - 1]
            if i > 1 and nums[i - 1] < nums[i - 2]:
                dp[i] -= dp[i - 2]
            if i > 1 and nums[i - 1] < nums[i - 2] and k > 0:
                dp[i] += dp[i - 2] - dp[i - 3]
                k -= 1
        return dp[n]

