            nonlocal max_path, max_path_count, max_path_count2
            max_path = max_path_count = max_path_count2 = 0
            for child, val in graph[node]:
                if child != parent:
                    path, count, count2 = dfs(child, node)
                    if val != nums[node]:
                        if path > max_path:
                            max_path_count2 = max_path_count
                            max_path_count = path
                        elif path == max_path:
                            max_path_count2 = max(max_path_count2, count)
                        elif path > max_path_count2:
