class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        :type edges: List[List[int]]
        :type nums: List[int]
        :rtype: List[int]
        """
        adj = [[] for _ in range(len(nums))]
        for u, v, _ in edges:
            adj[u].append((v, nums[v]))
            adj[v].append((u, nums[u]))

        def dfs(node, parent):
            nonlocal longest, second_longest, max_length
            longest = second_longest = 0
            for neighbor, val in adj[node]:
                if neighbor == parent:
                    continue
                longest_child, second_longest_child = dfs(neighbor, node)
                if val != nums[node]:
                    if longest_child > second_longest:
                        second_longest = longest_child
                    longest = longest_child
                if longest_child > second_longest:
                    second_longest = longest
                if longest > max_length:
                    max_length = longest
            if longest > 0:
                return longest + 1, second_longest
            return 0, 0

        max_length = 0
        longest, second_longest = dfs(0, -1)
        return [max_length, 1 + (longest > 0)]
