class Solution:
    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        """
        :type s: str
        :type p: str
        :rtype: int
        """
        def is_valid(i, j):
            if i < 0 or j < 0:
                return False
            if i >= len(s) or j >= len(p):
                return False
            if s[i] == p[j]:
                return True
            if p[j] == '*':
                return True
            return False

        def find_start(i, j):
            while i >= 0 and j >= 0 and is_valid(i, j):
                i -= 1
                j -= 1
            return i + 1, j + 1

        def find_end(i, j):
            while i < len(s) and j < len(p) and is_valid(i, j):
                i += 1
                j += 1
            return i - 1, j - 1

        i, j = 0, 0
        start, end = 0, 0
        min_len = float('inf')
        while i < len(s):
            if j < len(p) and s[i] == p[j]:
                j += 1
            elif j < len(p) and p[j] == '*':
                start, end = find_start(i, j)
                if end - start + 1 < min_len:
                    min_len = end - start + 1
                    start, end = find_start(i, j)
                i = end
                j += 1
            else:
                start, end = find_start(i, j)
                if end - start + 1 < min_len:
                    min_len = end - start + 1
                    start, end = find_start(i, j)
                i = end
                j = 0
        return min_len if min_len != float('inf') else -1
