            elif p[j] == '*':
                return is_valid(i + 1, j) or is_valid(i, j + 1)
            else:
                return False

        def find_min_length():
            """
            :rtype: int
            """
            min_length = float('inf')
            for i in range(len(s) - len(p) + 1):
                for j in range(len(p)):
                    if is_valid(i, j):
                        min_length = min(min_length, len(s) - i)
                        break
            return min_length

        return find_min_length() if find_min_length() != float('inf') else -1

