    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        """
        Returns the length of the shortest substring in s that matches p.
        If there is no such substring, returns -1.
        """
        def is_match(i, j):
            """
            Returns True if the substring s[i:j] matches p.
            """
            while i < j:
                if p[i] == '*':
                    if i == 0:
                        return is_match(i + 1, j)
                    if p[i - 1] == '*':
                        return is_match(i - 1, j) or is_match(i, j - 1)
                    if p[i - 1] == s[j - 1]:
                        return is_match(i, j - 1)
                    return False
                elif p[i] == s[j - 1]:
                    i += 1
                    j -= 1
                else:
                    return False
            return i == j

        n, m = len(s), len(p)
        if m == 0:
            return 0

        i, j = 0, 0
        min_len = float('inf')
        while i <= n - m:
            if is_match(i, i + m):
                min_len = min(min_len, j - i + 1)
                i += 1
            else:
                i += 1
                j = i

        return min_len if min_len != float('inf') else -1

