    def shortestMatchingSubstring(self, s: str, p: str) -> int:
        """
        Returns the length of the shortest substring in s that matches p.
        If there is no such substring, returns -1.
        """
        def is_valid(i, j):
            """
            Checks if the substring s[i:j] matches the pattern p.
            """
            if i == j:
                return p[i] == '*'
            if i == len(s) or j == len(p):
                return False
            if p[i] == '*':
                return is_valid(i+1, j) or (j < len(p) and is_valid(i, j+1))
            return s[i] == p[i] and is_valid(i+1, j+1)

        def find_shortest():
            """
            Finds the shortest matching substring in s that matches p.
            Returns the length of the shortest substring if found, otherwise -1.
            """
            for i in range(len(s)):
                for j in range(i, len(s)+1):
                    if is_valid(i, j):
                        return j - i
            return -1

        return find_shortest()

