class Solution:
    def minimumIncrements(self, nums: List[int], target: List[int]) -> int:
        """
        Returns the minimum number of operations required to make each element in target a multiple of at least one element in nums.
        """
        # Create a frequency dictionary for each element in target
        target_freq = {}
        for num in target:
            if num not in target_freq:
                target_freq[num] = 0
            target_freq[num] += 1

        # Create a frequency dictionary for each element in nums
        nums_freq = {}
        for num in nums:
            if num not in nums_freq:
                nums_freq[num] = 0
            nums_freq[num] += 1

        # Initialize the result variable
        result = 0

        # Iterate over each element in target
        for num in target:
            # If the element is not in nums, increment it by the minimum number of times it needs to be incremented
            if num not in nums_freq:
                min_increment = target_freq[num] - nums_freq.get(num, 0)
                result += min_increment
                # Update the frequency of the element in nums
                nums_freq[num] += min_increment

        return result
