    def minCostGoodCaption(self, caption: str) -> str:
        def next_char(c):
            return chr(ord(c) + 1) if c != 'z' else 'a'

        def prev_char(c):
            return chr(ord(c) - 1) if c != 'a' else 'z'

        n = len(caption)
        dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]
        dp[0][0] = 0

        for i in range(n):
            for j in range(n):
                if dp[i][j] == float('inf'):
                    continue
                for k in range(i + 1, n + 1):
                    if dp[i][j] + (next_char(caption[i]) != caption[k - 1]) <= dp[k][j + 1]:
                        dp[k][j + 1] = dp[i][j] + (next_char(caption[i]) != caption[k - 1])
                for k in range(j + 1, n + 1):
                    if dp[i][j] + (prev_char(caption[i]) != caption[k - 1]) <= dp[i + 1][k]:
                        dp[i + 1][k] = dp[i][j] + (prev_char(caption[i]) != caption[k - 1])

        min_cost = float('inf')
        best_caption = ""

        for i in range(n + 1):
            for j in range(n + 1):
                if dp[i][j] < min_cost:
                    min_cost = dp[i][j]
                    best_caption = caption[:i] + next_char(caption[i]) + caption[i + 1:j] + prev_char(caption[i]) + caption[j:]

        return best_caption if min_cost != float('inf') else ""
