            dp[0][0] = 0

            for i in range(n):
                for j in range(i + 1, n + 1):
                    if is_good(caption[i:j]):
                        dp[i][j] = dp[i][i] + 1
                    else:
                        dp[i][j] = min(dp[i][k] + dp[k][j] for k in range(i, j))

            return dp[0][n]

        min_cost = float('inf')
        best_caption = ""

        for i in range(n):
            for j in range(i + 1, n + 1):
                if is_good(caption[i:j]):
