    def largestInteger(self, nums: List[int], k: int) -> int:
        count = collections.Counter(nums)
        nums.sort()
        res = []
        for num in nums:
            if count[num] == 1:
                res.append(num)
                count[num] -= 1
            else:
                for i in range(len(res) - 1, -1, -1):
                    if res[i] % 2 == num % 2:
                        res.insert(i, num)
                        count[num] -= 1
                        break
                else:
