        return -1

class Solution:
    def largestInteger(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        from collections import defaultdict
        from itertools import combinations

        def is_almost_missing(num):
            for sub in combinations(nums, k):
                if num in sub:
                    return False
            return True

        num_count = defaultdict(int)
        for num in nums:
            num_count[num] += 1

        almost_missing_nums = [num for num in nums if is_almost_missing(num)]
        almost_missing_nums.sort(reverse=True)

        return almost_missing_nums[0] if almost_missing_nums else -1

class Solution:
    def largestInteger(self, nums: List[int], k: int) -> int:
        count = collections.Counter(nums)
        nums.sort()
        res = []
        for num in nums:
            if count[num] == 1:
                res.append(num)
                count[num] -= 1
            else:
                for i in range(len(res) - 1, -1, -1):
                    if res[i] % 2 == num % 2:
                        res.insert(i, num)
                        count[num] -= 1
                        break
                else:
                    res.append(num)
                    count[num] -= 1
        return max(res)

class Solution:
    def largestInteger(self, nums: List[int], k: int) -> int:
        """
        Returns the largest almost missing integer from nums.
        If no such integer exists, returns -1.
        """
        from collections import defaultdict
        from bisect import bisect_left

        num_to_count = defaultdict(int)
        for num in nums:
            num_to_count[num] += 1

        sorted_nums = sorted(num_to_count.keys())
        num_to_index = {num: bisect_left(sorted_nums, num) for num in num_to_count}

        def count(num):
            return num_to_count[num]

        def binary_search(num):
            left, right = 0, len(sorted_nums) - 1
            while left <= right:
                mid = (left + right) // 2
                if sorted_nums[mid] < num:
                    left = mid + 1
                else:
                    right = mid - 1
            return left

        for i in range(len(nums)):
            if count(nums[i]) == 1:
                num_to_index[nums[i]] = binary_search(nums[i])

        return max(num_to_index.keys(), key=lambda x: (-num_to_index[x], -count(x)))

