                if is_reachable(0) and check_threshold(0):
                    dfs2(0, parent, weights[0])
                    min_weight = min(min_weight, max(weights))

        return min_weight if min_weight != float('inf') else -1

class Solution:
    def minMaxWeight(self, n: int, edges: List[List[int]], threshold: int) -> int:
        def dfs(node, parent):
            visited[node] = True
            for neighbor, weight in graph[node]:
                if not visited[neighbor]:
                    dfs(neighbor, node)
                    if weight > threshold:
                        edges.append((node, neighbor, weight))

        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        visited = [False] * n
        for i in range(n):
            if not visited[i]:
                dfs(i, -1)

        if len(edges) < n - 1:
            return -1

        edges.sort(key=lambda x: x[2])
        max_weight = 0
        for i in range(len(edges)):
            node1, node2, weight = edges[i]
            if not visited[node1] or not visited[node2]:
                continue
            visited[node1] = False
            visited[node2] = False
            max_weight = max(max_weight, weight)
            for j in range(i + 1, len(edges)):
                node3, node4, weight = edges[j]
                if not visited[node3] or not visited[node4]:
                    continue
                visited[node3] = False
                visited[node4] = False
                max_weight = max(max_weight, weight)
                if max_weight > threshold:
                    return -1
        return max_weight

