class Solution:
    def minMaxSubarraySum(self, nums: List[int], k: int) -> int:
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        def helper(nums, k, start, end, memo):
            if (start, k) in memo:
                return memo[(start, k)]
            if k == 0:
                return 0
            if start == len(nums):
                return float('inf')
            if k == 1:
                return min(nums[start:])
            min_val = float('inf')
            max_val = float('-inf')
            for i in range(start, min(start + k, len(nums))):
                min_val = min(min_val, nums[i])
                max_val = max(max_val, nums[i])
            memo[(start, k)] = min_val + max_val + helper(nums, k - 1, start + 1, len(nums), memo)
            return memo[(start, k)]

        return helper(nums, k, 0, len(nums), {})
```

