class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def is_valid(x, y):
            return 0 <= x < len(grid) and 0 <= y < len(grid[0])

        def dfs(x, y, direction, sequence, length):
            if not is_valid(x, y):
                return 0

            if grid[x][y] != sequence:
                return 0

            if direction == 0:  # top-left to bottom-right
                if y + 1 < len(grid[0]) and grid[x][y + 1] == 2:
                    return dfs(x, y + 1, 1, 0, length + 1)
                elif y + 1 < len(grid[0]) and grid[x][y + 1] == 0:
                    return dfs(x, y + 1, 1, 0, length + 1)
                else:
                    return 0

            elif direction == 1:  # top-right to bottom-left
                if y - 1 >= 0 and grid[x][y - 1] == 2:
                    return dfs(x, y - 1, 2, 0, length + 1)
                elif y - 1 >= 0 and grid[x][y - 1] == 0:
                    return dfs(x, y - 1, 2, 0, length + 1)
                else:
                    return 0

            elif direction == 2:  # bottom-left to top-right
                if x + 1 < len(grid) and grid[x + 1][y] == 2:
                    return dfs(x + 1, y, 3, 0, length + 1)
                elif x + 1 < len(grid) and grid[x + 1][y] == 0:
                    return dfs(x + 1, y, 3, 0, length + 1)
                else:
                    return 0

            elif direction == 3:  # bottom-right to top-left
                if x - 1 >= 0 and grid[x - 1][y] == 2:
                    return dfs(x - 1, y, 0, 0, length + 1)
                elif x - 1 >= 0 and grid[x - 1][y] == 0:
                    return dfs(x - 1, y, 0, 0, length + 1)
                else:
                    return 0

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, dfs(i, j, 0, 0, 1))
                    max_length = max(max_length, dfs(i, j, 1, 0, 1))
                    max_length = max(max_length, dfs(i, j, 2, 0, 1))
                    max_length = max(max_length, dfs(i, j, 3, 0, 1))

        return max_length
