class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def dfs(i, j, direction, sequence, length):
            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 1:
                return 0

            if sequence == 0:
                sequence = 2
                length = 1
            else:
                sequence = (sequence + 1) % 4
                length += 1

            if direction == 0:
                if sequence == 0:
                    return 0
                elif sequence == 1:
                    return dfs(i + 1, j, 1, 0, length)
                elif sequence == 2:
                    return dfs(i + 1, j - 1, 2, 0, length)
                elif sequence == 3:
                    return dfs(i - 1, j + 1, 3, 0, length)
            elif direction == 1:
                if sequence == 0:
                    return 0
                elif sequence == 1:
                    return dfs(i + 1, j, 1, 0, length)
                elif sequence == 2:
                    return dfs(i + 1, j - 1, 2, 0, length)
                elif sequence == 3:
                    return dfs(i - 1, j + 1, 3, 0, length)
            elif direction == 2:
                if sequence == 0:
                    return 0
                elif sequence == 1:
                    return dfs(i + 1, j, 1, 0, length)
                elif sequence == 2:
                    return dfs(i + 1, j - 1, 2, 0, length)
                elif sequence == 3:
                    return dfs(i - 1, j + 1, 3, 0, length)
            elif direction == 3:
                if sequence == 0:
                    return 0
                elif sequence == 1:
                    return dfs(i + 1, j, 1, 0, length)
                elif sequence == 2:
                    return dfs(i + 1, j - 1, 2, 0, length)
                elif sequence == 3:
                    return dfs(i - 1, j + 1, 3, 0, length)

            return 1 + dfs(i + 1, j, direction, sequence, length)

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, dfs(i, j, 0, 0, 1))
                    max_length = max(max_length, dfs(i, j, 1, 0, 1))
                    max_length = max(max_length, dfs(i, j, 2, 0, 1))
                    max_length = max(max_length, dfs(i, j, 3, 0, 1))

        return max_length
