                    return dfs(x + 1, y + 1, 3, 0)
                else:
                    return 0

            if sequence == 3:
                if grid[x + 1][y] == 2:
                    return dfs(x + 1, y, 0, 2)
                elif grid[x + 1][y] == 0:
                    return dfs(x + 1, y, 0, 0)
                else:
                    return 0

