                for j in range(n):
                    for x in range(m):
                        for y in range(n):
                            if (x, y) != (i, j):
                                total_distance += abs(x - i) + abs(y - j)
            return total_distance

        return (calculate_manhattan_distance(m, n, k) * count_valid_arrangements(m, n, k)) % MOD
