    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7
        def factorial(n):
            if n == 0:
                return 1
            return n * factorial(n - 1) % MOD
        def binomial_coefficient(n, k):
            return factorial(n) // (factorial(k) * factorial(n - k)) % MOD
        def sum_of_distances(m, n, k):
            return (binomial_coefficient(m * n, k) * (m * n - k) * (m * n - k - 1) // 6) % MOD
        return sum_of_distances(m, n, k)

class Solution:
    def distanceSum(self, m: int, n: int, k: int) -> int:
        MOD = 10**9 + 7

        def count_ways(m, n, k):
            if k == 0:
                return 1
            if k > m * n:
                return 0
            return (count_ways(m - 1, n, k - 1) + count_ways(m, n - 1, k - 1)) % MOD

        def calculate_distance_sum(m, n, k):
            total_distance = 0
            for i in range(m):
                for j in range(n):
                    for x in range(m):
                        for y in range(n):
                            if (x, y) != (i, j):
                                total_distance = (total_distance + abs(i - x) + abs(j - y)) % MOD
            return total_distance

        return (count_ways(m, n, k) * calculate_distance_sum(m, n, k)) % MOD

