class Solution:
    def sortMatrix(self, grid: List[List[int]]) -> List[List[int]]:
        """
        Sorts the diagonals of a given n x n matrix in the specified order.

        :param grid: List[List[int]] - The input matrix of integers.
        :return: List[List[int]] - The matrix with diagonals sorted as required.
        """
        n = len(grid)
        diagonals = []

        # Collect diagonals into a list of lists
        for i in range(n):
            for j in range(n):
                if i - j == len(diagonals):
                    diagonals.append([])
                diagonals[i - j].append(grid[i][j])

        # Sort each diagonal in non-increasing order for bottom-left triangle
        for i in range(len(diagonals)):
            diagonals[i].sort(reverse=True)

        # Sort each diagonal in non-decreasing order for top-right triangle
        for i in range(len(diagonals)):
            if i < n - 1:
                diagonals[i].sort()

        # Place the sorted diagonals back into the matrix
        for i in range(n):
            for j in range(n):
                if i - j == len(diagonals):
                    grid[i][j] = diagonals[i - j].pop(0)

        return grid
