class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        n = len(nums)
        index_map = {}
        for i in range(n):
            if nums[i] not in index_map:
                index_map[nums[i]] = []
            index_map[nums[i]].append(i)

        result = []
        for q in queries:
            if q not in index_map:
                result.append(-1)
                continue
            indices = index_map[q]
            min_distance = float('inf')
            for i in indices:
                for j in indices:
                    if i == j:
                        continue
                    distance = abs(i - j)
                    distance = min(distance, n - distance)
                    min_distance = min(min_distance, distance)
            result.append(min_distance)

        return result
