class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        """
        Returns an array answer of the same size as queries, where answer[i] represents the result for query i.
        """
        n = len(nums)
        index_map = {}
        for i in range(n):
            if nums[i] not in index_map:
                index_map[nums[i]] = []
            index_map[nums[i]].append(i)

        result = []
        for i in queries:
            if nums[i] not in index_map:
                result.append(-1)
                continue

            indices = index_map[nums[i]]
            left, right = 0, len(indices) - 1
            while left < right:
                mid = (left + right) // 2
                if nums[indices[mid]] == nums[i]:
                    result.append(0)
                    break
                elif nums[indices[mid]] < nums[i]:
                    left = mid + 1
                else:
                    right = mid
            else:
                if left == 0:
                    result.append(abs(indices[0] - i))
                elif left == len(indices) - 1:
                    result.append(abs(indices[-1] - i))
                else:
                    result.append(min(abs(indices[left] - i), abs(indices[left - 1] - i)))

        return result
