class Solution:
    def maxFrequency(self, nums: List[int], k: int) -> int:
        nums.sort()
        i, j = 0, 0
        max_freq = 0
        while j < len(nums):
            if nums[j] - nums[i] <= k:
                max_freq = max(max_freq, j - i + 1)
                j += 1
