class Solution:
    def maxDifference(self, s: str) -> int:
        char_count = {}
        for char in s:
            if char in char_count:
                char_count[char] += 1
            else:
                char_count[char] = 1

        max_diff = 0
        for char, count in char_count.items():
            if count % 2 == 0:
                for other_char, other_count in char_count.items():
                    if other_char != char and other_count % 2 != 0:
                        max_diff = max(max_diff, other_count - count)

        return max_diff
