    def maxDifference(self, s: str) -> int:
        char_freq = {}
        for char in s:
            if char in char_freq:
                char_freq[char] += 1
            else:
                char_freq[char] = 1

        max_diff = 0
        for char, freq in char_freq.items():
            if freq % 2 == 0:
                for other_char, other_freq in char_freq.items():
                    if other_char != char and other_freq % 2 != 0:
                        max_diff = max(max_diff, other_freq - freq)

        return max_diff
