class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        n = len(s)
        dp = [[[-1 for _ in range(5)] for _ in range(5)] for _ in range(n + 1)]
        dp[0][0][0][0] = 0

        for i in range(n):
            for x in range(5):
                for y in range(5):
                    for d in range(4):
                        if s[i] == 'N':
                            dx, dy = 0, 1
                        elif s[i] == 'S':
                            dx, dy = 0, -1
                        elif s[i] == 'E':
                            dx, dy = 1, 0
                        else:
                            dx, dy = -1, 0

                        nx, ny = x + dx, y + dy
                        if 0 <= nx < 5 and 0 <= ny < 5:
                            dp[i + 1][nx][ny][d] = max(dp[i + 1][nx][ny][d], dp[i][x][y][d] + 1)
                        else:
                            dp[i + 1][nx][ny][d] = dp[i][x][y][d]

                        if d == 0:
                            dp[i + 1][x][y][1] = max(dp[i + 1][x][y][1], dp[i][x][y][0] + 1)
                        elif d == 1:
                            dp[i + 1][x][y][2] = max(dp[i + 1][x][y][2], dp[i][x][y][1] + 1)
                        elif d == 2:
                            dp[i + 1][x][y][3] = max(dp[i + 1][x][y][3], dp[i][x][y][2] + 1)
                        else:
                            dp[i + 1][x][y][3] = dp[i][x][y][3]

                        if k > 0:
                            if s[i] == 'N':
                                dp[i + 1][x][y][0] = max(dp[i + 1][x][y][0], dp[i][x][y][0] + 1)
                            elif s[i] == 'S':
                                dp[i + 1][x][y][1] = max(dp[i + 1][x][y][1], dp[i][x][y][1] + 1)
                            elif s[i] == 'E':
                                dp[i + 1][x][y][2] = max(dp[i + 1][x][y][2], dp[i][x][y][2] + 1)
                            else:
                                dp[i + 1][x][y][3] = max(dp[i + 1][x][y][3], dp[i][x][y][3] + 1)
                            k -= 1

        return max(max(max(dp[n][x][y][d] for d in range(4)) for y in range(5)) for x in range(5))
