            if s[i] == 'N':
                current_dist += 1
            elif s[i] == 'S':
                current_dist -= 1
            elif s[i] == 'E':
                current_dist += 1
            elif s[i] == 'W':
                current_dist -= 1

            if current_dist > max_dist:
                max_dist = current_dist

            if count < k:
                count += 1
            else:
                if s[i - count] == 'N':
                    current_dist -= 1
                elif s[i - count] == 'S':
                    current_dist += 1
                elif s[i - count] == 'E':
                    current_dist -= 1
                elif s[i - count] == 'W':
                    current_dist += 1
                count -= 1

        return max_dist
