class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * 5 for _ in range(k + 1)] for _ in range(n + 1)]

        def dfs(i, j, k):
            if i == n:
                return 0
            if dp[i][j][k] != -1:
                return dp[i][j][k]
            max_dist = 0
            for d in range(4):
                if j + d < 0 or j + d >= 5:
                    continue
                if k > 0:
                    max_dist = max(max_dist, dfs(i + 1, j + d, k - 1) + abs(i - n))
                else:
                    max_dist = max(max_dist, dfs(i + 1, j + d, k) + abs(i - n))
            dp[i][j][k] = max_dist
            return dp[i][j][k]

        return dfs(0, 0, k)
