        dp = [[[-1] * 5 for _ in range(5)] for _ in range(n)]

        def dfs(i, x, y, k):
            if i == n:
                return 0

            if dp[i][x][y] != -1:
                return dp[i][x][y]

            dx = [0, 0, 1, -1]
            dy = [1, -1, 0, 0]
            res = 0

            for d in range(4):
                nx, ny = x + dx[d], y + dy[d]
                if 0 <= nx < 5 and 0 <= ny < 5:
                    if s[i] == 'N':
                        res = max(res, 1 + dfs(i + 1, nx, ny, k))
                    elif s[i] == 'S':
                        res = max(res, 1 + dfs(i + 1, nx, ny, k))
                    elif s[i] == 'E':
                        res = max(res, 1 + dfs(i + 1, nx, ny, k))
                    elif s[i] == 'W':
                        res = max(res, 1 + dfs(i + 1, nx, ny, k))
                    else:
                        res = max(res, 1 + dfs(i + 1, nx, ny, k - 1))

            dp[i][x][y] = res
            return res

        return dfs(0, 0, 0, k)
