    def maxDistance(self, s: str, k: int) -> int:
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        n = len(s)
        dp = [[[-1] * (k + 1) for _ in range(2)] for _ in range(n)]

        def dfs(i, prev, k):
            if i == n:
                return 0

            if dp[i][prev][k] != -1:
                return dp[i][prev][k]

            if prev == 0:
                dp[i][prev][k] = max(dfs(i + 1, 1, k), dfs(i + 1, 2, k))
            elif prev == 1:
                dp[i][prev][k] = max(dfs(i + 1, 0, k), dfs(i + 1, 2, k) + 1)
            else:
                dp[i][prev][k] = max(dfs(i + 1, 0, k), dfs(i + 1, 1, k) + 1)

            if k > 0:
                dp[i][prev][k] = max(dp[i][prev][k], dfs(i + 1, prev, k - 1) + 1)

