    def maxScore(self, points: List[int], m: int) -> int:
        """
        Returns the maximum possible minimum value in gameScore after at most m moves.
        """
        def canReach(i, m, gameScore):
            """
            Determines if it is possible to reach index i with at most m moves.
            """
            if i < 0 or i >= len(points):
                return False
            if m == 0:
                return False
            if gameScore[i] > 0:
                return True
            return canReach(i + 1, m - 1, gameScore) or canReach(i - 1, m - 1, gameScore)

        def dfs(i, m, gameScore):
            """
            Recursively explores all possible moves from index i with at most m moves.
            """
            if i == len(points):
                return max(gameScore)
            if m == 0:
                return float('-inf')
            if gameScore[i] > 0:
                return dfs(i + 1, m, gameScore)
            return max(dfs(i + 1, m - 1, gameScore + [points[i]]), dfs(i - 1, m - 1, gameScore + [points[i]]))

        return dfs(-1, m, [0] * len(points))

