class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        """
        Finds the minimum y-coordinate value of a horizontal line such that the total area of the squares above the line equals the total area of the squares below the line.

        :param squares: List of squares, each represented as [x, y, l] where (x, y) is the bottom-left corner and l is the side length.
        :return: The minimum y-coordinate value of the line.
        """
        squares.sort(key=lambda x: x[1])  # Sort by y-coordinate

        total_area = 0
        for x, y, l in squares:
            total_area += l * l

        left, right = 0, 10**18  # Initial range for y-coordinate
        while left < right:
            mid = (left + right) / 2
            above = 0
            below = 0
            for x, y, l in squares:
                if y < mid:
                    above += l * l
                elif y > mid:
                    below += l * l
            if above == below:
                return mid
            elif above < below:
                left = mid + 1e-9  # Adjust for floating-point precision
            else:
                right = mid - 1e-9  # Adjust for floating-point precision

        return left
