class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        """
        :type squares: List[List[int]]
        :rtype: float
        """
        squares.sort(key=lambda x: (x[0], x[1]))
        total_area = sum(x[2] * x[2] for x in squares)
        left, right = 0, total_area
        while left < right:
            mid = (left + right) / 2
            if self.is_valid(squares, mid):
                right = mid
            else:
                left = mid + 1
        return left
    def is_valid(self, squares, y):
        """
        :type squares: List[List[int]]
        :type y: float
        :rtype: bool
        """
        total_area = 0
        for x, y1, l in squares:
            if y1 <= y:
                total_area += l * l
        return total_area == 0.5 * (total_area - self.total_area(squares, y))
    def total_area(self, squares, y):
        """
        :type squares: List[List[int]]
        :type y: float
        :rtype: float
        """
        total_area = 0
        for x, y1, l in squares:
            if y1 <= y:
                total_area += l * l
        return total_area
