class Solution:
    def minimumCost(self, nums: List[int], cost: List[int], k: int) -> int:
        """
        Returns the minimum total cost possible from any valid division.
        """
        n = len(nums)
        dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]
        dp[0][0] = 0

        for i in range(1, n + 1):
            for j in range(i + 1):
                dp[i][j] = dp[i - 1][j - 1] + (nums[i - 1] + (i - 1) * k) * (cost[i - 1] + (j - 1) * k)
                for l in range(j):
                    dp[i][j] = min(dp[i][j], dp[l][j - 1] + (nums[i - 1] + (i - 1) * k) * (cost[i - 1] + (j - 1 - l) * k))

        return dp[n][n]
